function(){
  var medium = {{URL.query.utm_medium}};

if (medium) {
 medium = medium; 
}
  else if ({{URL.query.gclid}}) {
    medium = "cpc";
  }
   else {
    medium = {{lookup.medium}};  
  }

return medium;
}

function() {

  var ts = Math.round(+new Date() / 1000.0);
  var rand;

  try{  
    var uu32 = new Uint32Array(1);
    rand = crypto.getRandomValues(uu32)[0];
  } catch(e) {
    rand = Math.round(Math.random() * 2147483647);
  }

  return [rand, ts].join('.');

}

function() {

  var desktopBreakpoint = 1004; //update with site specific breakpoint
  var tabletBreakpoint = 756; //update with site specific breakpoint

  function getResolution(desktopWidth, tabletWidth) {
      //breakpoints are based upon experimenting with the site
      if (window.innerWidth > desktopWidth) {
        return 'desktop';
      }
      else if (window.innerWidth > tabletWidth) {
          return 'tablet';
      }
      else {
          return 'mobile';
      }

      return undefined;
  }

  return getResolution(desktopBreakpoint, tabletBreakpoint);
}

function(){
  var source = {{URL.query.utm_source}}; 

if (source) {
 source = source;  
  } 
else if ({{URL.query.gclid}}) {
    source = "google";
  }
else {
 source = {{lookup.source}};  
}

return source;
}

